import React, { useState } from 'react';
import './App.css';

const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:5000';

function App() {
  const [proposals, setProposals] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [bookedRide, setBookedRide] = useState(null);
  const [booking, setBooking] = useState(false);
  const [cancelling, setCancelling] = useState(false);

  const searchRides = async () => {
    setLoading(true);
    setError(null);
    setProposals([]);
    setBookedRide(null);

    try {
      const response = await fetch(`${API_BASE}/api/search`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({}),
      });

      if (!response.ok) {
        throw new Error('Search failed');
      }

      const data = await response.json();
      const proposalsList = data.proposals || [];

      if (proposalsList.length === 0) {
        setError('No ride proposals available.');
      } else {
        setProposals(proposalsList);
      }
    } catch (err) {
      setError(err.message || 'Error searching for rides');
    } finally {
      setLoading(false);
    }
  };

  const bookRide = async (proposal) => {
    setBooking(true);
    setError(null);

    try {
      const response = await fetch(`${API_BASE}/api/book`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          prescheduled_ride_id: proposal.prescheduled_ride_id,
          proposal_uuid: proposal.proposal_uuid,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Booking failed');
      }

      const data = await response.json();
      setBookedRide({
        ...proposal,
        bookingResponse: data,
      });
    } catch (err) {
      setError(err.message || 'Error booking ride');
    } finally {
      setBooking(false);
    }
  };

  const cancelRide = async (rideId) => {
    setCancelling(true);
    setError(null);

    try {
      const response = await fetch(`${API_BASE}/api/cancel`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ride_id: rideId,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Cancellation failed');
      }

      setBookedRide(null);
      setProposals([]);
    } catch (err) {
      setError(err.message || 'Error cancelling ride');
    } finally {
      setCancelling(false);
    }
  };

  const formatTime = (timestamp) => {
    if (!timestamp) return 'N/A';
    try {
      const date = new Date(timestamp * 1000);
      return date.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit' });
    } catch {
      return 'N/A';
    }
  };

  const formatCost = (cost) => {
    if (!cost || cost === 0) return 'Free';
    return `$${cost.toFixed(2)}`;
  };

  return (
    <div className="app">
      <header className="header">
        <h1>RideSmart</h1>
      </header>

      <main className="main">
        {error && (
          <div className="error" role="alert">
            {error}
          </div>
        )}

        {!proposals.length && !loading && !bookedRide && (
          <div className="search">
            <button onClick={searchRides} className="button button-primary">
              Search for Rides
            </button>
          </div>
        )}

        {loading && (
          <div className="loading">
            Searching for rides...
          </div>
        )}

        {proposals.length > 0 && !bookedRide && (
          <div className="rides">
            <h2>Available Rides</h2>
            <div className="rides-list">
              {proposals.map((proposal, index) => {
                const rideInfo = proposal.ride_info || {};
                const pickup = rideInfo.pickup || {};
                const dropoff = rideInfo.dropoff || {};
                const pickupLoc = pickup.location || {};
                const dropoffLoc = dropoff.location || {};

                const pickupDesc = pickupLoc.short_description || pickupLoc.description || 'Unknown';
                const dropoffDesc = dropoffLoc.short_description || dropoffLoc.description || 'Unknown';
                const walkingDist = pickup.walking_distance_description || '';
                const eta = formatTime(pickup.eta_ts);
                const cost = formatCost(rideInfo.ride_cost);

                return (
                  <div key={proposal.proposal_uuid || index} className="ride-card">
                    <div className="ride-header">
                      <span className="ride-number">Ride {index + 1}</span>
                    </div>
                    <div className="ride-details">
                      <div className="ride-field">
                        <span className="label">Pickup:</span>
                        <span>{pickupDesc} {walkingDist && `(${walkingDist} walk)`}</span>
                      </div>
                      <div className="ride-field">
                        <span className="label">Dropoff:</span>
                        <span>{dropoffDesc}</span>
                      </div>
                      <div className="ride-field">
                        <span className="label">ETA:</span>
                        <span>{eta}</span>
                      </div>
                      <div className="ride-field">
                        <span className="label">Cost:</span>
                        <span>{cost}</span>
                      </div>
                    </div>
                    <button
                      onClick={() => bookRide(proposal)}
                      className="button button-book"
                      disabled={booking}
                    >
                      {booking ? 'Booking...' : 'Book Ride'}
                    </button>
                  </div>
                );
              })}
            </div>
            <button onClick={searchRides} className="button button-secondary">
              Search Again
            </button>
          </div>
        )}

        {bookedRide && (
          <div className="booked">
            <div className="booked-header">
              <h2>Ride Booked</h2>
            </div>
            <div className="booked-info">
              <div className="info-row">
                <span className="label">Ride ID:</span>
                <span>{bookedRide.prescheduled_ride_id}</span>
              </div>
              <div className="info-row">
                <span className="label">Proposal UUID:</span>
                <span>{bookedRide.proposal_uuid}</span>
              </div>
            </div>
            <div className="booked-actions">
              <button
                onClick={() => cancelRide(bookedRide.prescheduled_ride_id)}
                className="button button-cancel"
                disabled={cancelling}
              >
                {cancelling ? 'Cancelling...' : 'Cancel Ride'}
              </button>
              <button
                onClick={() => {
                  setBookedRide(null);
                  setProposals([]);
                }}
                className="button button-secondary"
              >
                New Search
              </button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
