import React, { useEffect, useState, useMemo } from 'react';
import { MapContainer, TileLayer, Marker, Polyline, Popup, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './RouteMap.css';

// Fix for default marker icons in react-leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-shadow.png',
});

// Custom icon for pickup (green)
const pickupIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-green.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

// Custom icon for dropoff (red)
const dropoffIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

// Custom icon for intermediate stops (orange)
const stopIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-orange.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.4/images/marker-shadow.png',
  iconSize: [20, 33],
  iconAnchor: [10, 33],
  popupAnchor: [1, -28],
  shadowSize: [33, 33]
});

// Component to fit map bounds to route
function FitBounds({ routePoints }) {
  const map = useMap();

  useEffect(() => {
    if (routePoints && routePoints.length > 0) {
      const bounds = L.latLngBounds(routePoints);
      map.fitBounds(bounds, { padding: [30, 30] });
    }
  }, [map, routePoints]);

  return null;
}

function RouteMap({ routeData, pickupLocation, dropoffLocation }) {
  const [mapReady, setMapReady] = useState(false);

  useEffect(() => {
    setMapReady(true);
  }, []);

  // Extract route points for the polyline
  const routePoints = useMemo(() => {
    if (!routeData || !routeData.route) return [];
    return routeData.route.map(point => [point.latlng.lat, point.latlng.lng]);
  }, [routeData]);

  // Extract stops from the route data
  const stops = useMemo(() => {
    if (!routeData || !routeData.stops) return [];
    return routeData.stops.map(stop => ({
      position: [stop.stop_location.latlng.lat, stop.stop_location.latlng.lng],
      isPickup: stop.pickups && stop.pickups.length > 0,
      isDropoff: stop.dropoffs && stop.dropoffs.length > 0,
      stopPointId: stop.stop_point_id
    }));
  }, [routeData]);

  // Default center (University of Chicago area)
  const defaultCenter = [41.788064, -87.601145];
  const defaultZoom = 14;

  if (!mapReady) {
    return <div className="route-map-loading">Loading map...</div>;
  }

  if (!routeData || !routeData.route || routeData.route.length === 0) {
    return (
      <div className="route-map-error">
        <p>No route data available</p>
      </div>
    );
  }

  return (
    <div className="route-map-container">
      <h3>üó∫Ô∏è Your Route</h3>
      <MapContainer
        center={defaultCenter}
        zoom={defaultZoom}
        style={{ height: '350px', width: '100%', borderRadius: '12px' }}
        className="route-map"
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {/* Fit map to show entire route */}
        <FitBounds routePoints={routePoints} />

        {/* Draw the route polyline */}
        <Polyline 
          positions={routePoints} 
          color="#8B0000" 
          weight={4}
          opacity={0.8}
        />

        {/* Show stops */}
        {stops.map((stop, index) => {
          let icon = stopIcon;
          let label = 'Stop';

          if (stop.isPickup) {
            icon = pickupIcon;
            label = 'Pickup';
          } else if (stop.isDropoff) {
            icon = dropoffIcon;
            label = 'Dropoff';
          }

          return (
            <Marker 
              key={`stop-${index}`} 
              position={stop.position} 
              icon={icon}
            >
              <Popup>
                <strong>{label}</strong>
                {stop.isPickup && <p>Your pickup point</p>}
                {stop.isDropoff && <p>Your dropoff point</p>}
              </Popup>
            </Marker>
          );
        })}
      </MapContainer>

      <div className="route-map-legend">
        <div className="legend-item">
          <span className="legend-marker pickup"></span>
          <span>Pickup</span>
        </div>
        <div className="legend-item">
          <span className="legend-marker dropoff"></span>
          <span>Dropoff</span>
        </div>
        <div className="legend-item">
          <span className="legend-line"></span>
          <span>Route</span>
        </div>
      </div>
    </div>
  );
}

export default RouteMap;
